const Hero = () => {
  const heroRef = useRef(null);
  const containerRef = useRef(null);
  const imageRef = useRef(null);
  const imageWrapperRef = useRef(null);
  const textGroupRef = useRef(null);
  const scrollIndicatorRef = useRef(null);
  const overlayRef = useRef(null);
  const whiteFadeRef = useRef(null);
  const releaseInfoRef = useRef(null);
  const maskedRectRef = useRef(null);
  const filledTextGroupRef = useRef(null);
  const textBrightRef = useRef(null);
  const textGlowRef = useRef(null);
  const platformsRef = useRef(null);

  useLayoutEffect(() => {
    const ctx = gsap.context(() => {
      const hero = heroRef.current;
      const textGroup = textGroupRef.current;
      const scrollIndicator = scrollIndicatorRef.current;
      const overlay = overlayRef.current;
      const whiteFade = whiteFadeRef.current;
      const releaseInfo = releaseInfoRef.current;
      const maskedRect = maskedRectRef.current;
      const filledTextGroup = filledTextGroupRef.current;
      const imageWrapper = imageWrapperRef.current;
      const textBright = textBrightRef.current;
      const textGlow = textGlowRef.current;
      const platforms = platformsRef.current;

      // gsap.set(textGroup, { scale: 20, opacity: 0, y: 0 });
      gsap.set(textGroup, { scale: 15, opacity: 0, y: 0 });
      gsap.set(overlay, { opacity: 0 });
      gsap.set(whiteFade, { opacity: 0 });
      gsap.set(releaseInfo, { opacity: 0, visibility: 'hidden' });
      gsap.set(filledTextGroup, { opacity: 0 });
      gsap.set(maskedRect, { opacity: 1 });
      gsap.set(imageWrapper, { filter: 'brightness(1)' });
      gsap.set(platforms, { opacity: 0 });

      function updateRadialMasks(progress) {
        let circleY, circleSize;
        if (progress <= 0.45) {
          const p = progress / 0.45;
          circleY = 150 - (p * 100);
          circleSize = p * 200;
        } else if (progress <= 0.55) {
          circleY = 50;
          circleSize = 200;
        } else {
          const p = (progress - 0.55) / 0.45;
          circleY = 50 - (p * 150);
          circleSize = 200 - (p * 200);
        }

        const brightMask = `radial-gradient(circle at 50% ${circleY}%, black 0%, black ${circleSize}%, transparent ${circleSize}%)`;
        const glowInner = Math.max(0, circleSize - 40);
        const glowOuter = circleSize + 20;
        const glowMask = `radial-gradient(circle at 50% ${circleY}%, transparent 0%, transparent ${glowInner}%, black ${circleSize}%, transparent ${glowOuter}%)`;

        if (textBright) {
          textBright.style.webkitMaskImage = brightMask;
          textBright.style.maskImage = brightMask;
        }
        if (textGlow) {
          textGlow.style.webkitMaskImage = glowMask;
          textGlow.style.maskImage = glowMask;
        }
      }

      updateRadialMasks(0);

      const tl = gsap.timeline({
        scrollTrigger: {
          trigger: hero,
          start: 'top top',
          end: '100% top',
          scrub: 1.5,
          pin: false,
        }
      });

      
      ///// 4 - MODIFIED FOR SLOWER IMAGE FADE 
    tl.to(textGroup, { opacity: 1, scale: 12, duration: 0.03, ease: 'none' }, 0);
    tl.to(textGroup, { scale: 5, duration: 0.09, ease: 'none' }, 0.03);
    tl.to(textGroup, { scale: 2.2, duration: 0.08, ease: 'none' }, 0.12);
    tl.to(textGroup, { scale: 1.2, duration: 0.05, ease: 'none' }, 0.20);

    // White fade - slower and less intense
    tl.to(whiteFade, { opacity: 0.7, duration: 0.25, ease: 'power1.in' }, 0.08);

    tl.to(maskedRect, { opacity: 0, duration: 0.01, ease: 'none' }, 0.25);
    tl.to(filledTextGroup, { opacity: 1, duration: 0.01, ease: 'none' }, 0.25);
    tl.to(overlay, { opacity: 1, duration: 0.05, ease: 'none' }, 0.25);
    tl.to(textGroup, { scale: 0.9, duration: 0.05, ease: 'none' }, 0.25);

    // White fade out - slower
    tl.to(whiteFade, { opacity: 0, duration: 0.15, ease: 'power1.out' }, 0.30);

    tl.to(textGroup, { scale: 0.5, y: -30, duration: 0.08, ease: 'none' }, 0.30);
    tl.to(textGroup, { scale: 0.22, y: -150, duration: 0.04, ease: 'none' }, 0.38);

    // Background image fades out SLOWLY - starts at 0.45 (when logo is already small)
    tl.to(imageWrapper, { opacity: 0, duration: 0.25, ease: 'power1.inOut' }, 0.45);

    tl.set(releaseInfo, { visibility: 'visible' }, 0.42);
    tl.to(releaseInfo, { opacity: 1, duration: 0.01, ease: 'none' }, 0.42);


      ScrollTrigger.create({
        trigger: hero,
        start: 'top top',
        end: '100% top',
        scrub: 0.5,
        onUpdate: (self) => {
          const scrollProgress = self.progress;
          const dimLayer = document.querySelector('.hero__text-dim');
          
          if (scrollProgress >= 0.42 && scrollProgress <= 0.92) {
            const wipeProgress = (scrollProgress - 0.42) / 0.50;
            updateRadialMasks(wipeProgress);
            
            if (wipeProgress > 0.55) {
              const fadeProgress = (wipeProgress - 0.55) / 0.45;
              if (dimLayer) dimLayer.style.opacity = String(1 - fadeProgress);
              const scale = 1 - (fadeProgress * 0.2);
              releaseInfo.style.transform = `translate(-50%, -25%) scale(${scale})`;
            } else {
              if (dimLayer) dimLayer.style.opacity = '1';
              releaseInfo.style.transform = 'translate(-50%, -25%) scale(1)';
            }
            
            if (wipeProgress > 0.35 && wipeProgress < 0.65) {
              gsap.to(platforms, { opacity: 1, duration: 0.2, overwrite: true });
            } else {
              gsap.to(platforms, { opacity: 0, duration: 0.2, overwrite: true });
            }
          } else if (scrollProgress < 0.42) {
            updateRadialMasks(0);
            gsap.set(platforms, { opacity: 0 });
            if (dimLayer) dimLayer.style.opacity = '1';
            releaseInfo.style.transform = 'translate(-50%, -25%) scale(1)';
          } else {
            updateRadialMasks(1);
            gsap.set(platforms, { opacity: 0 });
            if (dimLayer) dimLayer.style.opacity = '0';
            releaseInfo.style.transform = 'translate(-50%, -25%) scale(0.7)';
          }
        }
      });

      tl.to(releaseInfo, { opacity: 0, duration: 0.04, ease: 'none' }, 0.92);
      // tl.to(textGroup, { y: -500, opacity: 0, duration: 0.05, ease: 'none' }, 0.93);
      tl.to(textGroup, { y: -500, opacity: 0, duration: 0.05, ease: 'none' }, 0.93);

      tl.to(scrollIndicator, { opacity: 0, duration: 0.03, ease: 'none' }, 0);

    }, heroRef);

    return () => ctx.revert();
  }, []);

  return (
    <section ref={heroRef} className="hero" id="home">
      <div ref={containerRef} className="hero__container">
        <div ref={imageWrapperRef} className="hero__image">
          <img ref={imageRef} src={process.env.PUBLIC_URL + '/img/HighresScreenshot00014.webp'} alt="Cricket Stadium" />
        </div>
        <div ref={whiteFadeRef} className="hero__white-fade"></div>
        <div ref={overlayRef} className="hero__overlay"></div>

        {/* SVG with GOC Logo - viewBox matches original SVG dimensions */}
        <div className="hero__mask-container">
          {/* <svg className="hero__mask-svg" viewBox="0 0 1080 1080" preserveAspectRatio="xMidYMid slice"> */}
          <svg className="hero__mask-svg" viewBox="0 0 1080 1080" preserveAspectRatio="xMidYMid slice">
            <defs>
              {/* Mask definition - black fill creates the cutout */}
              <mask id="logoMask">
                <rect width="100%" height="100%" fill="white" />
                <GOCLogoPaths fill="black" />
              </mask>
            </defs>
            
            {/* Animated logo group */}
            <g ref={textGroupRef} style={{ transformOrigin: '540px 540px' }}>
              {/* Dark overlay with logo cutout (shows background through logo shape) */}
              <rect ref={maskedRectRef} width="1080" height="1080" fill="#0a0a0a" mask="url(#logoMask)" />
              {/* Filled white logo (fades in after transition) */}
              <g ref={filledTextGroupRef}>
                <GOCLogoPaths fill="rgba(255, 255, 255, 0.95)" />
              </g>
            </g>
          </svg>
        </div>

        <div ref={releaseInfoRef} className="hero__release-info">
          <div className="hero__text-dim">
            <div className="hero__coming">COMING</div>
            <div className="hero__date">NOVEMBER 19</div>
            <div className="hero__year">2026</div>
          </div>
          <div ref={textBrightRef} className="hero__text-bright">
            <div className="hero__coming">COMING</div>
            <div className="hero__date">NOVEMBER 19</div>
            <div className="hero__year">2026</div>
          </div>
          <div ref={textGlowRef} className="hero__text-glow">
            <div className="hero__coming">COMING</div>
            <div className="hero__date">NOVEMBER 19</div>
            <div className="hero__year">2026</div>
          </div>
          <div ref={platformsRef} className="hero__platforms">
            <span className="hero__platform">PS5</span>
            <span className="hero__platform-divider">|</span>
            <span className="hero__platform">XBOX SERIES X|S</span>
            <span className="hero__platform-divider">|</span>
            <span className="hero__platform">PC</span>
          </div>
        </div>

        <div ref={scrollIndicatorRef} className="hero__scroll">
          <div className="hero__scroll-icon"></div>
        </div>
      </div>
    </section>
  );
};

export default Hero;